mapper.select:
SELECT bg.id,bg.goods_no,bg.name,bg.brand,bg.category_code,bg.img,bg.tmall_url,bg.quarter,bg.year,bg.retail_price
FROM box_goods bg
left JOIN box_goods_attribute bga ON bg.id = bga.goods_id

    bg.deleted = 0

        and bg.category_code = #{categoryCode,jdbcType=VARCHAR}


        and bg.goods_no like CONCAT('%',#{goodsNo},'%')


        and bg.name like CONCAT('%',#{goodsName},'%')


        and bg.quarter like CONCAT('%',#{quarter},'%')


        and bg.year = #{year,jdbcType=INTEGER}


        and bg.retail_price >= #{lowPrice,jdbcType=DOUBLE}


        and bg.retail_price <= #{highPrice,jdbcType=DOUBLE}


        and bga.attribute_code in

            #{goodsAttributeCodes}




GROUP BY bg.id,bg.goods_no,bg.name,bg.brand,bg.category_code,bg.img,bg.tmall_url,bg.quarter,bg.year,bg.retail_price
order by add_time desc

SELECT bg.id,bg.goods_no,bg.name,bg.brand,bg.category_code,bg.img,bg.tmall_url,bg.quarter,bg.year,bg.retail_price,GROUP_CONCAT(bgs.id) AS sku_ids
FROM box_goods bg
left JOIN box_goods_attribute bga ON bg.id = bga.goods_id
left JOIN box_goods_sku bgs ON bg.id = bgs.goods_id

    bg.id is not NULL
    and bg.deleted = 0

        and bg.category_code in

            #{categoryCode}



        and bg.goods_no like CONCAT('%',#{goodsNo},'%')


        and bg.name like CONCAT('%',#{goodsName},'%')


        and bg.quarter like CONCAT('%',#{quarter},'%')


        and bg.year = #{year,jdbcType=VARCHAR}


        and bg.retail_price >= #{lowPrice,jdbcType=DOUBLE}


        and bg.retail_price <= #{highPrice,jdbcType=DOUBLE}


        and bga.attribute_code in

            #{goodsAttributeCodes}



        and bgs.color_name like CONCAT('%',#{colorName},'%')


        and bgs.color_code = #{colorCode,jdbcType=VARCHAR}


        and bgs.size_code = #{sizeCode,jdbcType=VARCHAR}


GROUP BY bg.id,bg.goods_no,bg.name,bg.brand,bg.category_code,bg.img,bg.tmall_url,bg.quarter,bg.year,bg.retail_price
order by bg.year desc

select bg.id,bg.goods_no,bg.name,bg.brand,bg.category_code,bg.img,bg.tmall_url,bg.quarter,bg.year,bg.retail_price,GROUP_CONCAT(bgs.id) AS sku_ids
from (
SELECT bg.id,bg.goods_no,bg.name,bg.brand,bg.category_code,bg.img,bg.tmall_url,bg.quarter,bg.year,bg.retail_price
FROM box_goods bg
LEFT JOIN box_goods_attribute bga ON bg.id = bga.goods_id

    and bg.deleted = 0

        and bg.category_code in

            #{categoryCode}



        and bg.goods_no like CONCAT('%',#{goodsNo},'%')


        and bg.name like CONCAT('%',#{goodsName},'%')


        and bg.quarter like CONCAT('%',#{quarter},'%')


        and bg.year = #{year,jdbcType=INTEGER}


        and bg.retail_price >= #{lowPrice,jdbcType=DOUBLE}


        and bg.retail_price <= #{highPrice,jdbcType=DOUBLE}


        and bga.attribute_code in

            #{goodsAttributeCodes}



        and bg.category_code not in

            #{avoidCategory}



        and bga.attribute_code not in

            #{avoidTexture}



        and bga.attribute_code not in

            #{avoidFigure}



GROUP BY bg.id,bg.goods_no,bg.name,bg.brand,bg.category_code,bg.img,bg.tmall_url,bg.quarter,bg.year,bg.retail_price
) bg
LEFT JOIN box_goods_sku bgs ON bg.id = bgs.goods_id

    1 = 1

        and bgs.color_name like CONCAT('%',#{colorName},'%')


        and bgs.color_code = #{colorCode,jdbcType=VARCHAR}


        and bgs.size_code = #{sizeCode,jdbcType=VARCHAR}


        and bgs.color_code not in

            #{avoidColor}


    /*衣服的尺寸，和价格区间过滤*/

        and (

           (bg.category_code in ('A','C','D','H','J','L','M','O','P','Q','U','V','W')   -- 上装

                and bgs.size_code = #{topSize,jdbcType=VARCHAR}


                and bg.retail_price >= #{topLowPrice}
                AND bg.retail_price <= #{topHighPrice}

            )
            or (bg.category_code in ('R','F')   -- 下装

                and bgs.size_code = #{bottomsSize,jdbcType=VARCHAR}


                and bg.retail_price >= #{bottomsLowPrice}
                AND bg.retail_price <= #{bottomsHighPrice}

            )

            or (bg.category_code in ('B')   -- 连衣裙

                and bgs.size_code = #{dressSize,jdbcType=VARCHAR}


                and bg.retail_price >= #{dressLowPrice}
                AND bg.retail_price <= #{dressHighPrice}

            )

            or (bg.category_code in ('N')   -- 牛仔裤

                and bgs.size_code = #{jeansSize,jdbcType=VARCHAR}

            )

            or (bg.category_code in ('S','E','T','Y','X') -- 外套

                and bg.retail_price >= #{overcoatLowPrice}
                AND bg.retail_price <= #{overcoatHighPrice}

            )
            or bg.category_code in ('G','K','I','V') -- 其他类别
        )


GROUP BY bg.id,bg.goods_no,bg.name,bg.brand,bg.category_code,bg.img,bg.tmall_url,bg.quarter,bg.year,bg.retail_price
order by bg.year desc
